void solve() {
    int n; cin &gt;&gt; n;
    string s; cin &gt;&gt; s;
    vi dp;
    int idx = 0;
    while (1) {
        int cnt = 0, mx = 0;
        while (idx &lt; n &amp;&amp; s[idx] == '1') idx ++;
        if (idx &gt;= n) break;
        while (1) {
            int num = 0;
            while (idx &lt; n &amp;&amp; s[idx] == '0') {
                idx ++; num ++;
            }
            if(idx == n) break;
            if(num == 1) cnt ++; 
            mx = max(mx, num);
            if (idx != n - 1 &amp;&amp; s[idx + 1] == '1') 
                break;
            idx ++; 
        }   
        if (cnt % 2 == 1 &amp;&amp; mx == 1) dp.pb(0);
        else dp.pb(1);
    }

    if (s[0] == '0') dp[0] = 1;
    if (s[n - 1] == '0') dp[sz(dp) - 1] = 1;

    for (int i = 0; i &lt; sz(dp); ++ i) {
        if (dp[i] == 0) {
            NO; return;
        }
    }
    YES;
}

