void solve() {
    int n, k; cin &gt;&gt; n &gt;&gt; k;
    vi v(n + 5);
    map&lt;int, int&gt; mp;
    for(int i = 0; i &lt; n; ++ i) {
        cin &gt;&gt; v[i];
        mp[v[i]] ++;
    }
    for(auto [x, y]: mp) {
        if(y % k != 0) {
            cout &lt;&lt; 0 &lt;&lt; "\n";
            return;
        }
    }
    map&lt;int, int&gt; mp1;
    int l = 0, r = 0;
    int ans = 0;
    while(l &lt; n &amp;&amp; r &lt; n) {
        mp1[v[r]] ++;
        while(mp1[v[r]] &gt; mp[v[r]] / k) {
            mp1[v[l]] --;
            l ++;
        }
        ans += r - l + 1;
        r ++;
    }
    cout &lt;&lt; ans &lt;&lt; "\n";
}

