const int N = 2e5 + 5;

vvi d;

void solve() {
    int n; cin &gt;&gt; n;
    vi v(n + 5);
    for(int i = 0; i &lt; n; ++ i) cin &gt;&gt; v[i];

    vi cnt(n + 5);
    int ans = 0, g = 0;

    for(int i = 0; i &lt; n; ++ i) {
        for(auto d: d[v[i]]) {
            cnt[d] ++;
            if(cnt[d] != i + 1) {
                ans = max(ans, cnt[d]);
            }
        }
        if(gcd(v[i], g) != g) {
            ans = max(ans, cnt[g]);
        }
        g = gcd(g, v[i]);
        cout &lt;&lt; ans &lt;&lt; " \n"[i == n - 1];
    }
}

signed main() {
    ios::sync_with_stdio(false); 
    cin.tie(nullptr);
    cout.tie(nullptr);

    d.assign(N + 1, {});

    for(int i = 1; i &lt;= N; ++ i) {
        for(int j = i; j &lt;= N; j += i) {
            d[j].push_back(i);
        }
    }

    int _ = 1;
    cin &gt;&gt; _;
    while(_ --) solve();

    return 0;
}

