void solve() {
    int n; cin &gt;&gt; n;
    string s; cin &gt;&gt; s;
    int cnta = count(all(s), 'a');
    int idx = (cnta + 1) / 2;
    int mid = 0;
    int cnt = 0;
    for(int i = 0; i &lt; n; ++ i) {
        if(s[i] == 'a') {
            cnt ++;
        }
        if(cnt == idx) {
            mid = i;
            break;
        }
    }
    int l = mid, r = mid;
    int ans1 = 0;
    for(int i = mid; i &gt;= 0; -- i) {
        if(s[i] == 'a') {
            ans1 += abs(l - i);
            l --;
        }
    }
    for(int i = mid; i &lt; n; ++ i) {
        if(s[i] == 'a') {
            ans1 += abs(i - r);
            r ++;
        }
    }
    int cntb = count(all(s), 'b');
    idx = (cntb + 1) / 2;
    mid = 0;
    cnt = 0;
    for(int i = 0; i &lt; n; ++ i) {
        if(s[i] == 'b') {
            cnt ++;
        }
        if(cnt == idx) {
            mid = i;
            break;
        }
    }
    int ans2 = 0;
    l = mid, r = mid;
    for(int i = mid; i &gt;= 0; -- i) {
        if(s[i] == 'b') {
            ans2 += abs(l - i);
            l --;
        }
    }
    for(int i = mid; i &lt; n; ++ i) {
        if(s[i] == 'b') {
            ans2 += abs(i - r);
            r ++;
        }
    }
    cout &lt;&lt; min(ans1, ans2) &lt;&lt; "\n";
}

