åŒæŒ‡é’ˆï¼Œå…ˆæ‰¾å‡ºæ‰€æœ‰å…ƒç´ æ•°é‡å°äºç­‰äº $k$ çš„åˆæ³•åŒºé—´çš„æ•°é‡ï¼Œå†æ‰¾å‡ºå…ƒç´ æ•°é‡å°äº $k$  çš„åˆæ³•åŒºé—´æ•°é‡ï¼Œå‡å»ä¹‹åï¼Œå°±æ˜¯å…ƒç´ æ•°é‡ç­‰äº $k$ çš„åˆæ³•åŒºé—´æ•°é‡ã€‚
void solve() {
    int n, k, L, R; cin &gt;&gt; n &gt;&gt; k &gt;&gt; L &gt;&gt; R;
    vi v(n);
    for(int i = 0; i &lt; n; ++ i) cin &gt;&gt; v[i];

    map&lt;int,int&gt; cnt;
    int d = 0;
    int l = 0;
    int ans1 = 0;

    for (int r = 0; r &lt; n; r++) {
        cnt[v[r]]++;
        if (cnt[v[r]] == 1) d++;

        while (d &gt; k) {
            cnt[v[l]]--;
            if (cnt[v[l]] == 0) d--;
            l++;
        }

        int minl = max(l, r - R + 1);
        int maxl = r - L + 1;
        if (maxl &gt;= minl) {
            ans1 += (ll)(maxl - minl + 1);
        }
    }
    map&lt;int,int&gt; cnt1;
    d = 0;
    l = 0;
    int ans2 = 0;
    k --;
    for (int r = 0; r &lt; n; r++) {
        cnt1[v[r]]++;
        if (cnt1[v[r]] == 1) d++;

        while (d &gt; k) {
            cnt1[v[l]]--;
            if (cnt1[v[l]] == 0) d--;
            l++;
        }

        int minl = max(l, r - R + 1);
        int maxl = r - L + 1;
        if (maxl &gt;= minl) {
            ans2 += (ll)(maxl - minl + 1);
        }
    }
    cout &lt;&lt; ans1 - ans2 &lt;&lt; "\n";
}

