void solve() {
    int n; cin &gt;&gt; n;
    vi v(n);
    for(int i = 0; i &lt; n; ++ i) cin &gt;&gt; v[i];
    for(int i = 0; i &lt; n; ++ i) {
        for(int j = i + 1; j &lt; n - 1; ++ j) {
            int a = 0, b = 0, c = 0;
            for(int k = 0; k &lt;= i; ++ k) {
                a += v[k];
            }
            for(int k = i + 1; k &lt;= j; ++ k) {
                b += v[k];
            }
            for(int k = j + 1; k &lt; n; ++ k) {
                c += v[k];
            }
            if(a % 3 == b % 3 &amp;&amp; b % 3 == c % 3) {
                cout &lt;&lt; i + 1 &lt;&lt; " " 
                &lt;&lt; j + 1 &lt;&lt; "\n";
                return;
            }
            if(a % 3 != b % 3 &amp;&amp; b % 3 != c % 3 
            &amp;&amp; c % 3 != a % 3) {
                cout &lt;&lt; i + 1 &lt;&lt; " " 
                &lt;&lt; j + 1 &lt;&lt; "\n";
                return;
            }
        }
    }
    cout &lt;&lt; "0 0 \n";
}

