void solve() {
    int n; cin &gt;&gt; n;
    vi v(n);
    map&lt;int, int&gt; mp;
    for(int i = 0; i &lt; n; ++ i) {
        cin &gt;&gt; v[i];
        mp[v[i]] ++;
    }
    vi nums;
    for(int i = n; i &gt;= 1; -- i) {
        if(mp[i] == 0) nums.pb(i);
    }
    int pos = 0;
    for(int i = 0; i &lt; n; ++ i) {
        if(v[i] == 0) v[i] = nums[pos ++];
    }

    int l = 0, r = n - 1;
    for(int i = 0; i &lt; n; ++ i) {
        if(v[i] == i + 1) l ++;
        else break;
    }
    for(int i = n - 1; i &gt; 0; -- i) {
        if(v[i] == i + 1) r --;
        else break;
    }
    if(r - l + 1 &lt;= 0) cout &lt;&lt; 0 &lt;&lt; "\n";
    else  cout &lt;&lt; r - l + 1 &lt;&lt; '\n';
}

