void solve() {
    int n; cin &gt;&gt; n;
    vi a(n), b(n);
    for(int i = 0; i &lt; n; ++ i) cin &gt;&gt; a[i];
    for(int i = 0; i &lt; n; ++ i) cin &gt;&gt; b[i];

    vi dp(2, 0), cur(2, 0);
    dp[0] = 1; dp[1] = 1; 
    for(int i = 1; i &lt; n; ++ i){
        cur[0] = cur[1] = 0;
        for(int j = 0; j &lt;= 1; ++ j){
            for(int k = 0; k &lt;= 1; ++ k){
                int p = (j == 0 ? a[i-1] : b[i-1]);
                int pp = (j == 0 ? b[i-1] : a[i-1]);
                int c = (k == 0 ? a[i] : b[i]);
                int cc = (k == 0 ? b[i] : a[i]);
                if(p &lt;= c &amp;&amp; pp &lt;= cc){
                    cur[k] += dp[j];
                    if(cur[k] &gt;= MOD) 
	                    cur[k] -= MOD;
                }
            }
        }
        dp = cur;
    }
    int ans = 0;
    if(n == 1) ans += 2 % MOD; 
    else {
        dp[0] += dp[1];
        if(dp[0] &gt;= MOD) dp[0] -= MOD;
        ans += dp[0];
    }
    cout &lt;&lt; ans &lt;&lt; '\n';
}

