void solve() {
    int n; cin &gt;&gt; n;
    vi v(n);
    int mx = n;
    for(int i = 0; i &lt; n; ++ i) {
        cin &gt;&gt; v[i];
    }

    int idx = 1, cnt = 0;
    for(int i = 0; i &lt; n; ++ i) {
        for(int j = 0; j &lt; n; ++ j) {
            if(v[j] != mx) continue;
            cnt = 0;
            while(j &lt; n &amp;&amp; v[j] == mx) {
                v[j ++] --;
                cnt ++;
            }
            if(cnt != idx) {
                NO;
                return;
            }
            idx ++;
            mx --;
            break;
        }
    }
    YES;
}


Bè´ªå¿ƒã€‚å°½å¯èƒ½ä½¿ç”¨åŒºé—´å°çš„æŠ˜æ‰£åŠµï¼Œå¯¹å°½å¯èƒ½è´µçš„éƒ¨åˆ†ä½¿ç”¨ï¼Œæ’ä¸ªåºå°±è¡Œã€‚æ³¨æ„å¯èƒ½åŠµçš„æ•°é‡ä¸å¤Ÿï¼Œéœ€è¦æ ¼å¤–å…¨é¢è´­ä¹°ä»·æ ¼ä½çš„å‰©ä½™çš„ç‰©å“
void solve() {
    int n, k; cin &gt;&gt; n &gt;&gt; k;
    vi v(n);
    for(int i = 0; i &lt; n ;++ i) cin &gt;&gt; v[i];

    vi m(k);
    for(int i = 0; i &lt; k; ++ i) cin &gt;&gt; m[i];

    sort(all(v), greater&lt;int&gt;());
    sort(all(m));

    int idx = 0, ans = 0;
    for(int i = 0; i &lt; k; ++ i) {
        int num = m[i];
        for(int i = 0; i &lt; num; ++ i) {
            if(i != num - 1) {
                ans += v[idx];
            }
            idx ++;
            if(idx &gt;= n) break;
        }
        if(idx &gt;= n) break;
    }
    for(int i = idx; i &lt; n; ++ i) {
        ans += v[i];
    }
    cout &lt;&lt; ans &lt;&lt; "\n";
}

Cå°†è¾ƒå°çš„æ•°æŒ‡å‘è¾ƒå¤§çš„æ•°ï¼Œå½¢æˆä¸€å¼ å›¾ï¼Œç„¶åæ‹“æ‰‘æ’åº
å…·ä½“æ¥è¯´ï¼š$$if (x &gt; y) : v \to u$$$$else: u \to v$$æ„æˆä¸€å¼ å›¾ï¼Œå¯¹æ‹“æ‰‘åºçš„æ¯ä¸€ä¸ªå…ƒç´ è¡¨ç¤ºçš„ä½ç½®ä¾æ¬¡é€’å¢
void solve() {
    int n; cin &gt;&gt; n;
    vvi G(n + 5);
    vi din(n + 5), tp;

    for(int i = 0; i &lt; n - 1; ++ i) {
        int u, v, x, y;
        cin &gt;&gt; u &gt;&gt; v &gt;&gt; x &gt;&gt; y;
        if(x &gt; y) {
            G[v].push_back(u);
            din[u] ++;
        } else {
            G[u].push_back(v);
            din[v] ++;
        }
    }

    function&lt;void()&gt; toposort = [&amp;]() {
        queue&lt;int&gt; q;
        for(int i=1;i&lt;=n;++i) 
            if(din[i]==0) q.push(i);
        while(!q.empty()) {
            int x = q.front();
            q.pop();
            tp.push_back(x);
            for(auto c : G[x]) 
                if(--din[c] == 0) q.push(c);
        }
        return tp.size() == n;
    };

    toposort();

    int idx = 1;
    vi ans(n + 5);
    for(int i: tp) 
        ans[i] = idx ++;

    for(int i = 1; i &lt;= n ;++ i) 
       cout &lt;&lt; ans[i] &lt;&lt; " \n"[i == n];
}

