    vector&lt;int&gt; prime;
    prime.reserve(n / 10); 
    vector&lt;char&gt; vis(n + 1);
    prime.pb(2);
    for (int i = 3; i &lt;= n; i += 2) {
        if (!vis[i]) prime.pb(i);
        for (int j = 0; i * prime[j] &lt;= n; ++ j) {
