void KMP(string S,string P){
    ll n = P.size(), m = S.size();
    for (int i = 1, j = 0; i &lt; n; ++i) {
        while(j &amp;&amp; P[i] != P[j])
            j = ne[j - 1];
        if (P[i] == P[j])
            j++;
        ne[i] = j;
    }
    for (int i = 0, j = 0; i &lt; m; ++i) {
        while(j &amp;&amp; S[i] != P[j])
            j = ne[j - 1];
        if (S[i] == P[j])
            j++;
        if(j == n){
            cout &lt;&lt; i - n + 2 &lt;&lt; '\n';
            j = ne[j - 1];
        }
    }
}
