å›¾è®ºdijkstraæœ€çŸ­è·¯struct Dijkstra {
    struct Edge { int v, w; };
    int n;
    vector&lt;vector&lt;Edge&gt;&gt; G;
    vector&lt;int&gt; d, vis, path, cnt;
    Dijkstra(int _n): n(_n) {
        G.assign(n + 1, {});
        d.assign(n + 1, inf);
        vis.assign(n + 1, 0);
        path.assign(n + 1, -1);
        cnt.assign(n + 1, 0);
    }
    void add(int u, int v, int w) {
        G[u].push_back({v, w});
    }
    void dijkstra(int s) {
        d[s] = 0;
        cnt[s] = 1;
        // å°é¡¶å †ï¼Œå­˜ {dist, u}
        using pii = pair&lt;int, int&gt;;
        priority_queue&lt;pii, vector&lt;pii&gt;, greater&lt;&gt;&gt; pq;
        pq.push({0, s});

        while (!pq.empty()) {
            auto [_u, u] = pq.top(); pq.pop();
            if (vis[u]) continue;
            vis[u] = 1;

            for (auto &amp;e : G[u]) {
                int v = e.v, w = e.w;
                if (d[v] &gt; d[u] + w) {
                    d[v] = d[u] + w;
                    path[v] = u;
                    cnt[v] = cnt[u];
                    pq.push({d[v], v});
                } else if (d[v] == d[u] + w) {
                    cnt[v] += cnt[u];
                }
            }
        }
    }

    // è·å–ä» s åˆ° u çš„è·¯å¾„ï¼Œè‹¥æ— è·¯å¾„è¿”å›ç©ºå‘é‡
    vector&lt;int&gt; getPath(int u, int s) {
        vector&lt;int&gt; res;
        if (d[u] == inf) return res;
        for (int cur = u; cur != -1; cur = path[cur]) {
            res.push_back(cur);
            if (cur == s) break;
        }
        reverse(res.begin(), res.end());
        return res;
    }
};
