class DSU {
private:
    int par[N],rk[N];
public:
    // åˆå§‹åŒ–nä¸ªå…ƒç´ 
    void init(int n) {
        for(int i = 1;i &lt;= n;++i) par[i] = i, rk[i] = 0;
    }
    // æŸ¥è¯¢æ ‘çš„æ ¹ (è·¯å¾„å‹ç¼©)
    int find(int x) {
        return par[x] = par[x] == x ? x : find(par[x]);
    }
    // æ™®é€šåˆå¹¶
    int unite(int x, int y) {
        x = find(x);y = find(y);
        return x == y ? 0 : par[x] = y;
    }
    // æŒ‰ç§©åˆå¹¶
    void merge(int x, int y) {
        x = find(x);y = find(y);
        if(x == y) return;
        if(rk[x] &gt; rk[y]) swap(x, y);
        par[x] = y;
        if(rk[x] == rk[y]) rk[y]++;
    }
    // åˆ¤æ–­xå’Œyæ˜¯å¦è”é€š
    bool same(int x, int y) {
        return find(x) == find(y);
    }
};
