class MST : DSU {
private:
    struct edge{
        int u, v, cost;
        bool operator&lt;(const edge&amp; other) const {
            return cost &lt; other.cost; // æŒ‰æƒå€¼å‡åºæ’åº
        }
    };
    vector&lt;edge&gt; es; // è¾¹é›†
    vector&lt;edge&gt; mst; //æœ€å°ç”Ÿæˆæ ‘è¾¹é›†
    int V, res; //V:ç‚¹æ•° res:MSTå„è¾¹é•¿ä¹‹å’Œ
public:
    MST(int n) : V(n) {} // æ„é€ å‡½æ•°, èµ‹å€¼Vä¸ºn
    int kruskal() {
        sort(es.begin(), es.end());
        init(V);
        mst.clear();
        res =  0;
        for(auto &amp;e : es) {
            if(!same(e.u, e.v)) {
                unite(e.u, e.v);
                mst.push_back(e);
                res += e.cost;
            }
        }
        return res;
    }
    // åŠ è¾¹, æƒ
    void addedge(int u, int v, int cost) {
        es.push_back({u, v, cost});
    }
    // åˆ¤æ–­æ˜¯å¦è¿é€š, å…ˆè¦ä½¿ç”¨kruskalç®—æ³•
    bool isConnected() {
        set&lt;int&gt; st;
        for (int i = 1; i &lt;= V; ++i) {
            st.insert(find(i));
        }
        return st.size() == 1;
    }
};
